mapper.select:
WITH A AS ( SELECT count(*) SAME_PLATE_NUM, CPZDBH FROM dwm_vlms_sptb02 GROUP BY CPZDBH )
SELECT da.source,da.info_code,da.vin, CASE WHEN b.CPZDBH != '' THEN A.SAME_PLATE_NUM ELSE 0 END, VVIN,HOST_COM_CODE,BASE_NAME,VEHICLE_NAME,START_CITY_NAME,END_CITY_NAME,VDWDM,DEALER_NAME, DDJRQ_R3,b.CPZDBH,ASSIGN_TIME,TRANSPORT_NAME,ACTUAL_OUT_TIME,SHIPMENT_TIME,VJSYDM,A.SAME_PLATE_NUM, DTVSDHSJ,FINAL_SITE_TIME,BRAND_NAME FROM `data_retrieve_detail` da left join dwm_vlms_sptb02 b on da.vin = b.VVIN and b.TRAFFIC_TYPE = 'G' LEFT JOIN A ON b.CPZDBH = A.CPZDBH WHERE da.info_code = #{param.infoCode} ORDER BY da.source desc
limit ${param.limitStart}, ${param.limitEnd}
