mapper.select:
SELECT
       *
FROM
    dwm_vlms_one_order_to_end_xi


        AND VIN = #{params.vin}


        AND BASE_NAME = #{params.baseName}


        AND TASK_NO = #{params.taskNo}


        AND STOWAGE_NOTE_NO = #{params.stowageNoteNo}


        AND LEAVE_FACTORY_TIME BETWEEN #{params.leaveFactoryTimeStart} AND #{params.leaveFactoryTimeEnd}


        AND START_CITY_NAME = #{params.startCityName}


        AND END_CITY_NAME = #{params.endCityName}


        AND IN_SITE_TIME BETWEEN #{params.inSiteTimeStart} AND #{params.inSiteTimeEnd}


        AND CP9_OFFLINE_TIME BETWEEN #{params.cp9OfflineTimeStart} AND #{params.cp9OfflineTimeEnd}


LIMIT  0,20
