mapper.select:
with  A as (
    select count(*) numbeT, STOWAGE_NOTE_NO  from
    dwm_vlms_one_order_to_end
    group by STOWAGE_NOTE_NO
)

SELECT
    case when STOWAGE_NOTE_NO != '' then A.numbeT  else 0 end as SAME_PLATE_NUM,
    VIN,BRAND,BASE_NAME,VEHICLE_NAME,CP9_OFFLINE_TIME,LEAVE_FACTORY_TIME,IN_SITE_TIME,IN_WAREHOUSE_NAME,TASK_NO,VEHICLE_RECEIVING_TIME,
    STOWAGE_NOTE_TIME,STOWAGE_NOTE_NO,TRAFFIC_TYPE,ASSIGN_TIME,CARRIER_NAME,ACTUAL_OUT_TIME,SHIPMENT_TIME,TRANSPORT_VEHICLE_NO,
    SAME_PLATE_NUM,VEHICLE_NUM,START_CITY_NAME,END_CITY_NAME,VDWDM,START_WATERWAY_NAME,IN_START_WATERWAY_TIME,END_START_WATERWAY_TIME,
    END_WATERWAY_NAME,IN_END_WATERWAY_TIME,START_PLATFORM_NAME,IN_START_PLATFORM_TIME,OUT_START_PLATFORM_TIME,END_PLATFORM_NAME,
    IN_END_PLATFORM_TIME,UNLOAD_SHIP_TIME,UNLOAD_RAILWAY_TIME,IN_DISTRIBUTE_TIME,
       DISTRIBUTE_ASSIGN_TIME,DISTRIBUTE_CARRIER_NAME,
    DISTRIBUTE_VEHICLE_NO,DISTRIBUTE_VEHICLE_NUM,OUT_DISTRIBUTE_TIME,DISTRIBUTE_SHIPMENT_TIME,DOT_SITE_TIME,FINAL_SITE_TIME
FROM
    dwm_vlms_one_order_to_end TOEND
left join A on TOEND.STOWAGE_NOTE_NO = A.STOWAGE_NOTE_NO



            and TOEND.VIN IN

                 #{value}




                AND TOEND.VIN = #{params.vin}




        AND TOEND.BASE_NAME = #{params.baseName}


        AND TOEND.TASK_NO = #{params.taskNo}


        AND TOEND.STOWAGE_NOTE_NO = #{params.stowageNoteNo}


        AND TOEND.LEAVE_FACTORY_TIME  >=  #{params.leaveFactoryTimeStart}


        AND TOEND.LEAVE_FACTORY_TIME  <=   #{params.leaveFactoryTimeEnd}


        AND TOEND.START_CITY_NAME = #{params.startCityName}


        AND TOEND.END_CITY_NAME = #{params.endCityName}


        AND TOEND.IN_SITE_TIME  >=  #{params.inSiteTimeStart}


        AND TOEND.IN_SITE_TIME  <=  #{params.inSiteTimeEnd}


        AND TOEND.CP9_OFFLINE_TIME  >=  #{params.cp9OfflineTimeStart}


        AND TOEND.CP9_OFFLINE_TIME  <=   #{params.cp9OfflineTimeEnd}


order by TOEND.CP9_OFFLINE_TIME desc


    limit ${params.limitStart}, ${params.limitEnd}

select count(*) from (
SELECT
VIN,BRAND,BASE_NAME,VEHICLE_NAME,CP9_OFFLINE_TIME,LEAVE_FACTORY_TIME,IN_SITE_TIME,IN_WAREHOUSE_NAME,TASK_NO,VEHICLE_RECEIVING_TIME,
STOWAGE_NOTE_TIME,STOWAGE_NOTE_NO,TRAFFIC_TYPE,ASSIGN_TIME,CARRIER_NAME,ACTUAL_OUT_TIME,SHIPMENT_TIME,TRANSPORT_VEHICLE_NO,
SAME_PLATE_NUM,VEHICLE_NUM,START_CITY_NAME,END_CITY_NAME,VDWDM,START_WATERWAY_NAME,IN_START_WATERWAY_TIME,END_START_WATERWAY_TIME,
END_WATERWAY_NAME,IN_END_WATERWAY_TIME,START_PLATFORM_NAME,IN_START_PLATFORM_TIME,OUT_START_PLATFORM_TIME,END_PLATFORM_NAME,
IN_END_PLATFORM_TIME,UNLOAD_SHIP_TIME,UNLOAD_RAILWAY_TIME,IN_DISTRIBUTE_TIME,DISTRIBUTE_ASSIGN_TIME,DISTRIBUTE_CARRIER_NAME,
DISTRIBUTE_VEHICLE_NO,DISTRIBUTE_VEHICLE_NUM,OUT_DISTRIBUTE_TIME,DISTRIBUTE_SHIPMENT_TIME,DOT_SITE_TIME,FINAL_SITE_TIME
FROM
dwm_vlms_one_order_to_end



            and VIN IN

                #{value}




                AND VIN = #{params.vin}




        AND BASE_NAME = #{params.baseName}


        AND TASK_NO = #{params.taskNo}


        AND STOWAGE_NOTE_NO = #{params.stowageNoteNo}


        AND LEAVE_FACTORY_TIME  >=  #{params.leaveFactoryTimeStart}


        AND LEAVE_FACTORY_TIME  <=   #{params.leaveFactoryTimeEnd}


        AND START_CITY_NAME = #{params.startCityName}


        AND END_CITY_NAME = #{params.endCityName}


        AND IN_SITE_TIME  >=  #{params.inSiteTimeStart}


        AND IN_SITE_TIME  <=  #{params.inSiteTimeEnd}


        AND CP9_OFFLINE_TIME  >=  #{params.cp9OfflineTimeStart}


        AND CP9_OFFLINE_TIME  <=   #{params.cp9OfflineTimeEnd}


order by CP9_OFFLINE_TIME desc
)

SELECT COUNT(VIN) AS SAME_PLATE_NUM,STOWAGE_NOTE_NO FROM dwm_vlms_one_order_to_end
STOWAGE_NOTE_NO = #{stowageNoteNo}
GROUP BY STOWAGE_NOTE_NO
