sql.where:
AND (VIN, WAREHOUSE_UPDATETIME) IN (
SELECT VIN, MAX(WAREHOUSE_UPDATETIME)
FROM dwm_vlms_one_order_to_end_01



            and VIN IN

                #{value}




                AND VIN = #{params.vin}




        AND BASE_NAME = #{params.baseName}


        AND TASK_NO = #{params.taskNo}


        AND STOWAGE_NOTE_NO = #{params.stowageNoteNo}


        AND LEAVE_FACTORY_TIME  >=  #{params.leaveFactoryTimeStart}


        AND LEAVE_FACTORY_TIME  <=   #{params.leaveFactoryTimeEnd}


        AND START_CITY_NAME = #{params.startCityName}


        AND END_CITY_NAME = #{params.endCityName}


        AND IN_SITE_TIME  >=  #{params.inSiteTimeStart}


        AND IN_SITE_TIME  <=  #{params.inSiteTimeEnd}


        AND CP9_OFFLINE_TIME  >=  #{params.cp9OfflineTimeStart}


        AND CP9_OFFLINE_TIME  <=   #{params.cp9OfflineTimeEnd}


GROUP BY VIN)
