mapper.select:
SELECT count(dvs.CJSDBH) AS totalNum FROM dwm_vlms_sptb02_test1 dvs
(dvs.CJSDBH,dvs.WAREHOUSE_UPDATETIME) IN ( SELECT CJSDBH, MAX(WAREHOUSE_UPDATETIME) FROM dwm_vlms_sptb02_test1
AND THEORY_SITE_TIME BETWEEN #{params.startTime} AND #{params.endTime}
AND THEORY_SITE_TIME != 0
AND CQWH = #{params.cqwh}
AND CZJGSDM = #{params.czjgsdm}
GROUP BY CJSDBH )

SELECT count(dvs.CJSDBH) AS totalNum FROM dwm_vlms_sptb02_test1 dvs
(dvs.CJSDBH,dvs.WAREHOUSE_UPDATETIME) IN ( SELECT CJSDBH, MAX(WAREHOUSE_UPDATETIME) FROM dwm_vlms_sptb02_test1
AND THEORY_SITE_TIME BETWEEN #{params.startTime} AND #{params.endTime}
AND FINAL_SITE_TIME != 0
AND FINAL_SITE_TIME < THEORY_SITE_TIME
OR FINAL_SITE_TIME = 0
AND THEORY_SITE_TIME > toUnixTimestamp64Milli(now64(3))
AND CQWH = #{params.cqwh}
AND CZJGSDM = #{params.czjgsdm}
GROUP BY CJSDBH )

