mapper.select:
select COUNT(aa.CJSDBH) as totalNum, (select COUNT(1) from dwm_vlms_sptb02 dvs1 WHERE FINAL_SITE_TIME !=0 and FINAL_SITE_TIME < dvs1.THEORY_SITE_TIME) as finalNum, (select COUNT(1) from dwm_vlms_sptb02 dvs2 WHERE FINAL_SITE_TIME = 0 and #{params.endTime} < dvs2.THEORY_SITE_TIME) as nowNum, (nowNum+finalNum) / totalNum * 100 as percentage from dwm_vlms_sptb02 aa where (CJSDBH, WAREHOUSE_UPDATETIME) in ( select CJSDBH, max(WAREHOUSE_UPDATETIME) from dwm_vlms_sptb02 dvs
and dvs.DDJRQ BETWEEN #{params.startTime}and #{params.endTime}
and dvs.CQWH = #{params.cqwh}
and dvs.CZJGSDM = #{params.czjgsdm}
group by dvs.CJSDBH )
