mapper.select:
SELECT count(dvs.CJSDBH) AS totalNum FROM dwm_vlms_sptb02 dvs
(dvs.CJSDBH,dvs.WAREHOUSE_UPDATETIME) IN ( SELECT CJSDBH, MAX(WAREHOUSE_UPDATETIME) FROM dwm_vlms_sptb02
THEORY_SITE_TIME != 0
AND THEORY_SITE_TIME BETWEEN #{params.startTime} AND #{params.endTime}
AND CQWH = #{params.cqwh}
AND CZJGSDM = #{params.czjgsdm}
GROUP BY CJSDBH )

select dvs.CJSDBH,SHIPMENT_TIME AS SHIPMENT_TIME,FINAL_SITE_TIME AS FINAL_SITE_TIME,CQWH,CZJGSDM,BASE_NAME AS
BASE_NAME,CUSTOMER_NAME as CUSTOMER_NAME,MAX(WAREHOUSE_UPDATETIME)
from dwm_vlms_sptb02 dvs
where
dvs.SHIPMENT_TIME != 0
and dvs.SHIPMENT_TIME  <=  #{params.endTime}
and (dvs.FINAL_SITE_TIME  >  #{params.startTime} or dvs.FINAL_SITE_TIME = 0)

    AND dvs.CQWH = #{params.cqwh}


    AND dvs.CZJGSDM = #{params.czjgsdm}

GROUP BY dvs.CJSDBH,dvs.SHIPMENT_TIME,dvs.FINAL_SITE_TIME,dvs.CQWH,dvs.CZJGSDM,dvs.BASE_NAME,CUSTOMER_NAME

    limit ${params.limitStart}, ${params.limitEnd}

sql.where:
HOST_COM_CODE = '1'


        AND VVIN IN

            #{value}




            AND VVIN = #{params.vvin}




    AND BASE_NAME = #{params.baseName}


    AND ACTUAL_OUT_TIME  >=  #{params.actualOutTimeStart}


    AND ACTUAL_OUT_TIME  <=   #{params.actualOutTimeEnd}


    AND DDJRQ_R3  >=  #{params.vehiclePlateIssuedTimeStart}


    AND DDJRQ_R3  <=   #{params.vehiclePlateIssuedTimeEnd}


    AND START_CITY_NAME = #{params.startCityName}


    AND END_CITY_NAME = #{params.endCityName}


    AND DTVSDHSJ   >=  #{params.dtvsdhsjStart}


    AND DTVSDHSJ   <=   #{params.dtvsdhsjEnd}


    AND FINAL_SITE_TIME   >=  #{params.finalSiteTimeStart}


    AND FINAL_SITE_TIME  <=   #{params.finalSiteTimeEnd}


    AND TRAFFIC_TYPE = #{params.trafficType}


    AND CPZDBH = #{params.cpzdbh}


    AND CCXDL IN

        #{value}

mapper.select:
WITH A AS ( SELECT count(*) SAME_PLATE_NUM, CPZDBH FROM dwm_vlms_sptb02 GROUP BY CPZDBH ) SELECT CASE WHEN CPZDBH != '' THEN A.SAME_PLATE_NUM ELSE 0 END, VVIN,HOST_COM_CODE,BASE_NAME,VEHICLE_CODE, VEHICLE_NAME,START_CITY_NAME,END_CITY_NAME,VDWDM,DEALER_NAME, DDJRQ_R3,CPZDBH,ASSIGN_TIME,TRANSPORT_NAME,ACTUAL_OUT_TIME,SHIPMENT_TIME,VJSYDM,A.SAME_PLATE_NUM, DTVSDHSJ,FINAL_SITE_TIME,BRAND_NAME FROM dwm_vlms_sptb02 SPTB02 LEFT JOIN A ON SPTB02.CPZDBH = A.CPZDBH
ORDER BY SPTB02.DDJRQ_R3 DESC
limit ${params.limitStart}, ${params.limitEnd}

WITH A AS ( SELECT count(*) SAME_PLATE_NUM, CPZDBH FROM dwm_vlms_sptb02 GROUP BY CPZDBH ) SELECT CASE WHEN CPZDBH != '' THEN A.SAME_PLATE_NUM ELSE 0 END, VVIN,HOST_COM_CODE,BASE_NAME,VEHICLE_CODE, VEHICLE_NAME,START_CITY_NAME,END_CITY_NAME,VDWDM,DEALER_NAME, DDJRQ_R3,CPZDBH,ASSIGN_TIME,TRANSPORT_NAME,ACTUAL_OUT_TIME,SHIPMENT_TIME,VJSYDM,A.SAME_PLATE_NUM, DTVSDHSJ,FINAL_SITE_TIME,BRAND_NAME FROM dwm_vlms_sptb02 SPTB02 LEFT JOIN A ON SPTB02.CPZDBH = A.CPZDBH
ORDER BY SPTB02.DDJRQ_R3 DESC
limit ${params.limitStart}, ${params.limitEnd}
