mapper.select:
SELECT IDNUM, VIN , CP , OPERATETIME
          FROM ods_vlms_base_station_data_epc
          where
              OPERATETIME >= #{startDateStr}
            AND OPERATETIME <= #{endDateStr}

limit #{limitStart} ,
#{limitEnd};

mapper.insert:
insert into dwm_vlms_one_order_to_end (VIN

    , BASE_CODE


    , BASE_NAME

    ) VALUES (
#{params.VIN}

    , #{params.BASE_CODE}


    , #{params.BASE_NAME}

) ON DUPLICATE KEY UPDATE
VIN = VALUES(VIN)

    BASE_CODE = VALUES(BASE_CODE)


    , BASE_NAME = VALUES(BASE_NAME)

insert into ods_vlms_one_order_to_end (
    VIN

    , IN_WAREHOUSE_CODE


    , IN_WAREHOUSE_NAME

    )  values (
    #{params.vin}

    , #{params.inWarehouseCode}


    , #{params.inWarehouseName}

) ON DUPLICATE KEY UPDATE
VIN = VALUES(VIN) ,

    IN_WAREHOUSE_CODE=VALUES(IN_WAREHOUSE_CODE)


    , IN_WAREHOUSE_NAME=VALUES(IN_WAREHOUSE_NAME)

mapper.update:
UPDATE dwm_vlms_one_order_to_end
SET CP9_OFFLINE_TIME = #{OPERATETIME}
WHERE
    VIN = #{VIN}
  AND ( CP9_OFFLINE_TIME = 0 OR CP9_OFFLINE_TIME > #{OPERATETIME} ) ;

UPDATE dwm_vlms_one_order_to_end e
    JOIN dim_vlms_warehouse_rs a
SET IN_SITE_TIME = #{SAMPLE_U_T_C}
WHERE e.VIN = #{VIN} ,
    AND e.LEAVE_FACTORY_TIME < #{SAMPLE_U_T_C}
    AND a.WAREHOUSE_TYPE = 'T1'

UPDATE dwm_vlms_one_order_to_end d
JOIN ods_vlms_base_station_data o
SET d.LEAVE_FACTORY_TIME = #{SAMPLE_U_T_C}
WHERE
d.VIN = #{VIN}
AND d.CP9_OFFLINE_TIME < #{SAMPLE_U_T_C}
AND ( d.LEAVE_FACTORY_TIME = 0 OR d.LEAVE_FACTORY_TIME > #{SAMPLE_U_T_C} )
AND (o.SHOP_NO = 'DZCP901' OR o.SHOP_NO = 'DZCP9' )
AND o.OPERATE_TYPE='OutStock'
